mapper.select:
SELECT * FROM documents
 WHERE doc_status = #{status}
   AND emp_no = #{emp_no}
 ORDER BY doc_reg_date DESC, doc_no DESC

SELECT MAX(doc_no)
  FROM documents
 WHERE emp_no = #{emp_no}

mapper.insert:
INSERT INTO work_report(doc_no, emp_no, title, content, coop_dept_no, execution_date)
VALUES(
    #{doc_no}, 
    #{emp_no}, 
    #{title}, 
    #{content}, 
    #{coop_dept_no}
    null, 
    #{execution_date}
)

mapper.update:
UPDATE documents
   SET doc_status = 1
 WHERE doc_no = #{doc_no}

UPDATE approval_lines
   SET appr_status = 4
     , appr_date = now()

	 , appr_comment = #{appr_comment}

 WHERE doc_no = #{doc_no}
   AND appr_no = #{appr_no}

UPDATE documents
   SET doc_status = 4
 WHERE doc_no = #{doc_no}

UPDATE approval_lines
   SET appr_status = #{appr_status}
     , appr_date = now()

	 , appr_comment = #{appr_comment}

 WHERE doc_no = #{doc_no}
   AND appr_no = #{appr_no}

mapper.select:
SELECT MAX(appr_order)
  FROM approval_lines
 WHERE doc_no = #{doc_no}

mapper.update:
UPDATE documents
   SET doc_status = 3
 WHERE doc_no = #{doc_no}

UPDATE documents
   SET doc_title = #{doc_title}
   	 , doc_reg_date = now()
   	 , doc_status = #{doc_status}
   	 , doc_urgent = #{doc_urgent}
 WHERE doc_no = #{doc_no}

UPDATE work_report
   SET title = #{title}
   	 , content = #{content}

   	 , coop_dept_no = #{coop_dept_no}

   	 , execution_date = #{execution_date}
 WHERE doc_no = #{doc_no}
