mapper.select:
SELECT * FROM documents
 WHERE doc_status = #{status}
   AND emp_no = #{emp_no}
 ORDER BY doc_reg_date DESC

SELECT MAX(doc_no)
  FROM documents
 WHERE emp_no = #{emp_no}

mapper.insert:
INSERT INTO work_report(doc_no, emp_no, title, content, coop_dept_no, execution_date)
VALUES(
    #{doc_no}, 
    #{emp_no}, 
    #{title}, 
    #{content}, 
    #{coop_dept_no}
    null, 
    #{execution_date}
)
