mapper.select:
	select * from attendance
 	where emp_no = #{emp_no}
 	 and  date = curdate();

		SELECT 
		    SUM(normal) AS normal, 
		    SUM(late) AS late, 
		    SUM(early_leave) AS early_leave, 
		    SUM(out_status) AS out_status, 
		    SUM(absent) AS absent
		FROM attendance
		where emp_no = #{emp_no}
		 AND MONTH(date) = #{month}
 		 AND YEAR(date) = #{year}
