mapper.select:
	select * from attendance
 	where emp_no = #{emp_no}
 	 and  date = curdate()

 SELECT *
	FROM attendance
	where emp_no = #{emp_no}
	 AND MONTH(date) = #{month}
	 AND YEAR(date) = #{year}

     SELECT a.*, e.emp_name, e.emp_img
	    FROM attendance a
	    JOIN employees e ON a.emp_no = e.emp_no
	    WHERE a.emp_no = #{emp_no}
	      AND MONTH(a.date) = #{month}
	      AND YEAR(a.date) = #{year}
	      AND a.message IS NOT NULL
